mapper.update:
UPDATE
FROM t_todo
SET cleaning = #{cleaning},
do_day = #{doDay}
WHERE todo_id = #{todoId} AND user_id = #{userId}

mapper.delete:
DELETE
FROM t_todo
WHERE todo_id = #{todoId} AND user_id = #{userId}

mapper.select:
SELECT is_checked
FROM t_todo
where todo_id = #{todoId} AND user_id = #{userId}
