mapper.update:
UPDATE t_todo
SET cleaning = #{cleaning},
do_day = #{doDay}
WHERE todo_id = #{todoId} AND user_id = #{loginedUserId}

UPDATE t_todo
SET is_del = #{isDel}
WHERE todo_id = #{todoId} AND user_id = #{loginedUserId}

mapper.select:
SELECT is_checked
FROM t_todo
where todo_id = #{todoId} AND user_id = #{loginedUserId}

